	private void jButtonParkStatusActionPerformed(java.awt.event.ActionEvent evt) throws Exception {
		// TODO add your handling code here:
		String path = "database.csv";

		MaintainDatabase maintain = new MaintainDatabase();

		maintain.load(path);

		for (Database d : maintain.databaseList) {
			if ((d.getParkingLot() == Integer.valueOf(jComboBoxParkingSpot.getSelectedItem().toString()))
					&& d.getLicense().equals(jTextFieldLicense1.getText())) {
				if (d.getPaid() == true) {
					jLabelPayment.setText("Paid");
				} else {
					jLabelPayment.setText("Didn't Pay");
				}
				jLabelBookID.setText(Integer.valueOf(d.getbookID()).toString());
				jLabelDuration.setText(Integer.valueOf(d.getDuration()).toString());
				jLabelTime.setText(d.getTime());
				
				

			} else {
				jLabelBookID.setText("User not found!");
				jLabelPayment.setText("Parking Spot Vacant!");
				
			}

		}
		
		
	}

