	private void jButtonGrantRequestActionPerformed(java.awt.event.ActionEvent evt) throws Exception {
		// TODO add your handling code here:
		String path = "database.csv";
		MaintainDatabase maintain = new MaintainDatabase();
		

		
		maintain.load(path);

			for(Database d: maintain.databaseList){
			if((d.getParkingLot() == Integer.valueOf(jComboBoxParkingSpot.getSelectedItem().toString())) && d.getLicense().equals(jTextFieldLicense1.getText())) {
				
				
					d.setValidated(true);
					maintain.update(path);
					jLabelVerfication.setText("Request Granted!");
				
				
			}
		
			}
	}

	private void jButtonLogoutActionPerformed(java.awt.event.ActionEvent evt) throws Exception {
		// TODO add your handling code here:
		String path = "user.csv";
    	MaintainUser main = new MaintainUser();
    	 MaintainUser.logout(MaintainUser.emailString);
         main.update(path);
    	LandingGUI OP = new LandingGUI();
        OP.setVisible(true);
        OP.pack();
        OP.setLocationRelativeTo(null);
        OP.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        this.dispose();
	}

