	private void jButtonCancelRequestActionPerformed(java.awt.event.ActionEvent evt) throws Exception {
		// TODO add your handling code here:
		String path = "database.csv";
		MaintainDatabase maintain = new MaintainDatabase();
		int parkingSpot = Integer.valueOf(jComboBoxParkingSpot.getSelectedItem().toString());
		String license = jTextFieldLicense1.getText();
        
	
			maintain.load(path);
			for(Database d: maintain.databaseList){
				
			
				if(parkingSpot == d.getParkingLot() && license.equals(d.getLicense())) {
				maintain.databaseList.remove(d);
				maintain.update(path);
				jLabelVerfication.setText("Cancelled Request!");
				
				}
			
		}
	}

